mapper.select:
SELECT count(*) FROM stock WHERE 1=1
AND prodName like CONCAT('%',#{prodName},'%')
AND prodCategory = #{prodCategory}

SELECT A.* FROM( SELECT @ROWNUM := @ROWNUM + 1 AS no ,prodCode AS prodCode ,prodName AS prodName ,prodCategory AS prodCategory ,stockCnt AS stockCnt ,originPrice AS originPrice ,salePrice AS salePrice ,regDate AS regDate ,regId AS regId ,uptDate AS uptDate ,uptId AS uptId FROM stock, (SELECT @ROWNUM := 0) R WHERE 1=1
AND prodName like CONCAT('%',#{prodName},'%')
AND prodCategory = #{prodCategory}
) A
WHERE A.no >= ((#{pageNo}-1)*10 + 1) AND A.no <= ((#{pageNo}-1)*10 + 10)
